<template>  <div class="randimg">    <img v-bind:style="{top: imgTop + 'px', left: imgLeft + 'px',height: imgHeight + 'px', width: imgWidth + 'px'}" class="image" v-bind:src="vue">  </div></template><script>  const vue = require("../assets/images/vue.png");  export default {    name: "randimg",    data() {      return {        vue,          imgTop: 0,          imgLeft: 0,          imgHeight: 64,          imgWidth: 64      }    },    created() {        this.imgTop = Math.round(Math.random() * (screen.height - this.imgHeight));        this.imgLeft = Math.round(Math.random() * (screen.width - this.imgWidth));    }  }</script><style>  .image {    position: absolute;    z-index: -1;    opacity: 0.5;  }</style>